import React, { useState } from 'react';
import '../styleesheets/Login.css';
import logo from '../imagenes/logo-container.png'; 
import axios from 'axios';

function Login({ onLogin }) {

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState(null);

    const login = async (username, password) => {
        try {
            const response = await axios.post('https://localhost:8010/api/login', {
                usuario: username,
                contrasena: password,
            });

            return { token: response.data.token }
        } catch (error) {
            return { error: true };
        }
    };

    const handdlelogin = async (e) => {
        e.preventDefault();

        const response = await login(username, password)

        if (response.error) {
            setError(response.error);
          } else {
            onLogin(response.token); // Iniciar sesión con éxito utilizando el token devuelto
          }
    }

    return (
        <div className='login-container'>

            <div className='login-form'>
                <img src={logo} alt='Header Graphic' className='header-image'/> {/* Usando logo-container como imagen de encabezado */}
                <input onChange={(event) => {setUsername(event.target.value)}} className='usuario-input' type='text' placeholder='Usuario' required/>
                <input onChange={(event) => {setPassword(event.target.value)}} className='password-input' type='password' placeholder='Password' required/>


                <div className='checkbox-container'>
                    <input type='checkbox' id='rememberMe'/>
                    <label htmlFor='rememberMe'>Recordar usuario</label>
                </div>
                <button className='iniciar-btn' onClick={handdlelogin}>Iniciar sesión</button>
            </div>
            {error && <div className="error-message">Usuario o contraseña incorrectos</div>}

            <footer>
                Derechos Reservados Leonhardt Machinics Services
            </footer>
        </div>
    );
}

export default Login;
