import React from "react";
import "../styleesheets/Dashboard.css";
import "../styleesheets/Proveedores.css";
import { useState, useEffect } from "react"; // Asegúrate de importar useEffect
import Axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";

function Proveedores() {
  const [razonSocial, setRazonSocial] = useState("");
  const [direccion, setDireccion] = useState("");
  const [telefono, setTelefono] = useState("");
  const [rut, setRut] = useState("");
  const [banco, setBanco] = useState("");
  const [numeroCuenta, setNumeroCuenta] = useState("");
  const [tipoCuenta, setTipoCuenta] = useState("");
  const [proveedorList, setProveedores] = useState([]);

  useEffect(() => {
    getProveedores();
  }, []);

  const registrarProvedores = () => {
    Axios.post("http://localhost:3001/create", {
      razonSocial: razonSocial,
      direccion: direccion,
      telefono: telefono,
      rut: rut,
      banco: banco,
      numeroCuenta: numeroCuenta,
      tipoCuenta: tipoCuenta,
    })
      .then(() => {
        alert("Proveedor Registrado!!");
        getProveedores(); // Actualiza la lista después de registrar
      })
      .catch((error) => {
        console.error("Hubo un error al registrar:", error.response);
      });
  };

  const getProveedores = () => {
    Axios.get("http://localhost:3001/proveedorList")
      .then((response) => {
        setProveedores(response.data);
      })
      .catch((error) => {
        console.error("Hubo un error al obtener proveedores:", error.response);
      });
  };

  return (
    <main>
      <div className="container">
        <div className="lista">
          {proveedorList.map((val) => {
            return <div key={val.id}>{val.id}</div>;
          })}
        </div>
        <div class="card text-center">
          <div className="card-header">REGISTRAR PROVEEDORES</div>
          <div className="card-body">
            <div className="input-group mb-3">
              <span className="input-group-text" id="basic-addon1">
                Razon Social:
              </span>
              <input
                type="text"
                onChange={(event) => {
                  setRazonSocial(event.target.value);
                }}
                className="form-control"
                placeholder="Ingrese Razon Social "
                aria-label="Username"
                aria-describedby="basic-addon1"
              />
            </div>
            <div className="datos-proveedores">
              <div className="input-group mb-3">
                <span className="input-group-text" id="basic-addon1">
                  Direccion:
                </span>
                <input
                  type="text"
                  onChange={(event) => {
                    setDireccion(event.target.value);
                  }}
                  className="form-control"
                  placeholder="Ingrese Direccion "
                  aria-label="Username"
                  aria-describedby="basic-addon1"
                />
              </div>
              <div className="input-group mb-3">
                <span className="input-group-text" id="basic-addon1">
                  Telefono:
                </span>
                <input
                  type="text"
                  onChange={(event) => {
                    setTelefono(event.target.value);
                  }}
                  className="form-control"
                  placeholder="Ingrese Telefono "
                  aria-label="Username"
                  aria-describedby="basic-addon1"
                />
              </div>
              <div className="input-group mb-3">
                <span className="input-group-text" id="basic-addon1">
                  Rut:
                </span>
                <input
                  type="text"
                  onChange={(event) => {
                    setRut(event.target.value);
                  }}
                  className="form-control"
                  placeholder="Ingrese Rut Empresa "
                  aria-label="Username"
                  aria-describedby="basic-addon1"
                />
              </div>
              <div className="input-group mb-3">
                <span className="input-group-text" id="basic-addon1">
                  Banco:
                </span>
                <input
                  type="text"
                  onChange={(event) => {
                    setBanco(event.target.value);
                  }}
                  className="form-control"
                  placeholder="Ingrese Banco "
                  aria-label="Username"
                  aria-describedby="basic-addon1"
                />
              </div>
              <div className="input-group mb-3">
                <span className="input-group-text" id="basic-addon1">
                  Numero Cuenta:
                </span>
                <input
                  type="text"
                  onChange={(event) => {
                    setNumeroCuenta(event.target.value);
                  }}
                  className="form-control"
                  placeholder="Ingrese Numero Cuenta "
                  aria-label="Username"
                  aria-describedby="basic-addon1"
                />
              </div>

              <div className="input-group mb-3">
                <span className="input-group-text" id="basic-addon1">
                  Tipo Cuenta:
                </span>
                <input
                  type="text"
                  onChange={(event) => {
                    setTipoCuenta(event.target.value);
                  }}
                  className="form-control"
                  placeholder="Ingrese Tipo de Cuenta "
                  aria-label="Username"
                  aria-describedby="basic-addon1"
                />
              </div>

              <button className="btn btn-success" onClick={registrarProvedores}>
                Registrar
              </button>
            </div>
          </div>
          <div class="card-footer text-body-secondary"></div>
        </div>
        <table class="table table-striped">
          <thead>
            <tr>
              <th scope="col">#</th>
              <th scope="col">Razon Social</th>
              <th scope="col">Direccion</th>
              <th scope="col">Telefono</th>
              <th scope="col">Rut</th>
              <th scope="col">Banco</th>
              <th scope="col">Numero Cuenta</th>
              <th scope="col">Tipo Cuenta</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <th scope="row">1</th>
              <td>Mark</td>
              <td>Otto</td>
              <td>@mdo</td>
              <td>Mark</td>
              <td>Otto</td>
              <td>@mdo</td>
              <td>@mdo</td>
            </tr>
          </tbody>
        </table>
      </div>
    </main>
  );
}

export default Proveedores;
