import React from 'react';

function NotificarWhatsapp() {
    const [titulo, setTitulo] = React.useState('');
    const [mensaje, setMensaje] = React.useState('');
    const [whatsapp, setWhatsapp] = React.useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        const response = await fetch(`https://api.whatsapp.com/send?phone=${whatsapp}&text=${titulo}%0A${mensaje}`);
        if(response.ok) {
            alert('Mensaje enviado a WhatsApp');
        } else {
            alert('Error al enviar el mensaje');
        }
    };

    return (
        <div>
            <h1>Enviar notificación a WhatsApp</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    Título:
                    <input type="text" value={titulo} onChange={e => setTitulo(e.target.value)} />
                </label>
                <br />
                <label>
                    Mensaje:
                    <textarea value={mensaje} onChange={e => setMensaje(e.target.value)} />
                </label>
                <br />
                <label>
                    Número de WhatsApp (con código de país):
                    <input type="text" value={whatsapp} onChange={e => setWhatsapp(e.target.value)} />
                </label>
                <br />
                <button type="submit">Enviar notificación</button>
            </form>
        </div>
    );
}

export default NotificarWhatsapp;
