import React from "react";
import { useReactTable } from "@tanstack/react-table";
import { useState, useEffect } from "react";    
import Axios from "axios";

function ListadoUsuarios(){
    const [usuariosList, setUsuarios] = useState([]);
    const token = localStorage.getItem('token');
    const columnas = [
        {
            header: "ID",
            accesorKey: 'id'
        },
        {
            header: "Nombre",
            accesorKey: 'nombreCompletoUsuario'
        },
        {
            header: "Teléfono",
            accesorKey: 'telefonoUsuario'
        },
        {
            header: "Email",
            accesorKey: 'emailUsuario'
        },
        {
            header: "Tipo Perfil",
            accesorKey: 'tipoPerfil'
        },
        {
            header: "Estado",
            accesorKey: 'estadoUsuario'
        },
    ]
    useEffect(() => {
        listarUsuarios();
    }, []);

    const listarUsuarios = async() =>{
        await Axios.get("http://localhost:8010/api/usuarios",{headers: {'Authorization': token,},})
        .then((response) => {setUsuarios(response.data);})
        .catch((error) => {console.error("Hubo un error al obtener la lista de usuarios:", error.response);});
    };
    const tabla = useReactTable({usuariosList,columnas});
    return(
        <div>
            <table>
                <thead>
                    {
                       tabla.getHeaderGroups().flatMap(headerGroup=> (
                            <tr key={headerGroup.id}>
                                {
                                    headerGroup.headers.flatMap(header=>(
                                        <th key={header.id}>
                                            {header.column.columnDef.header}
                                        </th>
                                    ))
                                }
                            </tr>
                        )) 
                    }
                </thead>
                <tbody>

                </tbody>
                <tfoot>

                </tfoot>
            </table>
        </div>
    )
}

export default ListadoUsuarios
