import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../styleesheets/Dashboard.css';
const images = require.context('../icons/', true)



const dashboardItems = [
  { iconPath: 'icons-cliente.png', altText: 'Clientes', buttonText: 'Clientes', path: '/clientes', className: 'fa fa-address-book' },
  { iconPath: 'icons-tareas.png', altText: 'Trabajos', buttonText: 'Trabajos', path: '/trabajos', className: 'fa fa-cogs' },
  { iconPath: 'icons-proveedores.png', altText: 'Proveedores', buttonText: 'Proveedores', path: '/proveedores', className: 'fa fa-lock' },
  { iconPath: 'icons-usuarios.png', altText: 'Mantenedor Usuarios', buttonText: 'Usuarios', path: '/registrarusuario', className: 'fa fa-lock' },
  { iconPath: 'icons-inventario.png', altText: 'Inventario', buttonText: 'Inventario', path: '/inventario', className: 'fa fa-lock' },
  { iconPath: 'icons-gastos.png', altText: 'Gastos', buttonText: 'Gastos', path: '/gastos', className: 'fa fa-lock' }
];

function Sidebar({ onLogout }) {
const navigate = useNavigate();

const handleLogout = () => {
  onLogout();
  navigate('/');
};

return (
  <nav className='sidebar'>
    <ul>
      {dashboardItems.map((item, index) => (
        <li key={index}>
            <Link to={item.path}><span className={item.className}>&nbsp;</span>{item.buttonText}</Link>
        </li>
      ))}
      <li>
        <button onClick={handleLogout}>Cerrar Sesi√≥n</button>
      </li>
    </ul>
  </nav>
);
}

export default Sidebar;
