import axios from 'axios';

const login = async (username, password) => {
    try {
        const response = await axios.post('http://localhost:8010/api/login', {
            usuario: username,
            contrasena: password,
        });

        return { token: response.data.token }
    } catch (error) {
        return { error: true };
    }
};

module.exports = {
    login
}
