import React from 'react';
import '../styleesheets/Dashboard.css';
import TrabajosCurso from './Dashboard/TrabajosCurso';
import TrabajosEspera from './Dashboard/TrabajosEspera';
import TrabajosFinalizados from './Dashboard/TrabajosFinalizados';
import ListadoTrabajosDashBoard from './Dashboard/ListadoTrabajosDashBoard';
import Axios from "axios";
import { useState, useEffect } from "react";   

function Dashboard() {
    const [trabajosList, setTrabajos] = useState([]);
    const token = localStorage.getItem('token');
    const columnas = [
        {
            header: "ID",
            accesorKey: 'idTrabajo'
        }
    ]

    useEffect(() => {
        listarTrabajos();
    }, []);

    const listarTrabajos = async() =>{
        await Axios.get("http://localhost:8010/api/trabajos/listadoTrabajosAdmin",{headers: {'Authorization': token,},})
        .then((response) => {setTrabajos(response.data);})
        .catch((error) => {console.error("Hubo un error al obtener la lista de usuarios:", error.response);});
    };

    return (
        <main>
            <div className='row'>
                <div className='col-sm-12 col-md-12 col-lg-12'>
                    <div className='card'>
                        <div className='card-header'>
                            <h3><span className='fa fa fa-tachometer'></span>&nbsp;Informaci√≥n General</h3>
                        </div>
                        <div className='card-body'>
                            <div className='row'>
                                <div className="col-sm-3 col-md-3 col-lg-3"></div>
                                <div className='col-lg-2'>
                                    <TrabajosCurso/>
                                </div>
                                <div className='col-lg-2'>
                                    <TrabajosEspera/>
                                </div>
                                <div className='col-lg-2'>
                                    <TrabajosFinalizados/>
                                </div>
                                <div className="col-sm-3 col-md-3 col-lg-3"></div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            <br></br>
            <div className='row'>
                <div className='col-sm-12 col-md-12 col-lg-12'>
                    <div className='card'>
                        <div className='card-body'>
                            <div className='row'>
                                <ListadoTrabajosDashBoard data={trabajosList} columns={columnas}/>
                            </div>
                        </div>
                    </div> 
                </div>
            </div>
        </main>
    );

}

export default Dashboard;
