import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../styleesheets/Dashboard.css';
const images = require.context('../icons/', true)



const dashboardItems = [
  { iconPath: 'icons-cliente.png', altText: 'Clientes', buttonText: 'Clientes', path: '/clientes' },
  { iconPath: 'icons-tareas.png', altText: 'Trabajos', buttonText: 'Trabajos', path: '/trabajos' },
  { iconPath: 'icons-proveedores.png', altText: 'Proveedores', buttonText: 'Proveedores', path: '/proveedores' },
  { iconPath: 'icons-usuarios.png', altText: 'Registrar Usuario', buttonText: 'Registrar Usuario', path: '/registrarusuario' },
  { iconPath: 'icons-inventario.png', altText: 'Inventario', buttonText: 'Inventario', path: '/inventario' },
  { iconPath: 'icons-gastos.png', altText: 'Gastos', buttonText: 'Gastos', path: '/gastos' }
];

function Sidebar({ onLogout }) {
const navigate = useNavigate();

const handleLogout = () => {
  onLogout();
  navigate('/');
};

return (
  <nav className='sidebar'>
    <ul>
      {dashboardItems.map((item, index) => (
        <li key={index}>
            <img src={images(`./${item.iconPath}`)} alt={item.altText} />
            <Link to={item.path}>{item.buttonText}</Link>
        </li>
      ))}
      <li>
        <button onClick={handleLogout}>Cerrar Sesi√≥n</button>
      </li>
    </ul>
  </nav>
);
}

export default Sidebar;
