import React from "react";
import { useState, useEffect } from "react";    
import Axios from "axios";

function InformacionDashboard(props){
    let labelInformacion;
    let nombreClase;
    switch(props.idEstadoTrabajo){
        case 2:
            labelInformacion = "En Espera de Insumos";
            nombreClase = "fa fa-hourglass-end"
            break;
        case 3: 
            labelInformacion = "Trabajos Finalizados";
            nombreClase = "fa fa-check"
            break;
        default:
            labelInformacion = "Trabajos Finalizados.";
            nombreClase = "fa fa-wrench"
            break;
    }
    const [getTrabajos, setTrabajos] = useState([]);
    const token = localStorage.getItem('token');
    useEffect(() => {
        dashboardTrabajos();
    }, []);
    const dashboardTrabajos = async() =>{
        await Axios.get("http://localhost:8010/api/datosDashboard/" + props.idEstadoTrabajo ,{headers: {'Authorization': token,},})
        .then((response) => {setTrabajos(response.data);})
        .catch((error) => {console.error("Hubo un error al obtener el conteo de trabajos:", error.response);});
    };
    return(
        <div className="row">
            <div className="col-sm-12 col-md-12 col-lg-12"></div>
            <div className='card'>
                <div className='card-header'>
                    <span className={nombreClase}></span>&nbsp;{labelInformacion}
                </div>
                <div className='card-body'>
                    <span className="text-center">
                        <h1>
                        {getTrabajos.map((val) => {
                            return (
                                <span key={val.id}>
                                    {val.totalRegistros}
                                </span>
                                );
                            })}
                        </h1>
                    </span>
                </div>
            </div>
        </div>
    )
}
export default InformacionDashboard
