import React from "react";
import { useState, useEffect } from "react";    
import Axios from "axios";

function TrabajosCurso(){
    const [getTrabajos, setTrabajos] = useState([]);
    const token = localStorage.getItem('token');
    useEffect(() => {
        dashboardTrabajos();
    }, []);
    const dashboardTrabajos = async() =>{
        await Axios.get("http://localhost:8010/api/datosDashboard/1",{headers: {'Authorization': token,},})
        .then((response) => {setTrabajos(response.data);})
        .catch((error) => {console.error("Hubo un error al obtener el conteo de trabajos:", error.response);});
    };
    return(
        <div className="row">
            <div className="col-sm-1 col-md-1 col-lg-1"></div>
            <div className="col-sm-10 col-md-10 col-lg-10"></div>
            <div className='card'>
                <div className='card-header'>
                    <h5><span className='fa fa fa-wrench'></span>&nbsp;Trabajos en Curso</h5>
                </div>
                <div className='card-body'>
                    <span>
                        <h1>
                        {getTrabajos.map((val) => {
                            return (
                                <span key={val.id}>
                                    {val.totalRegistros}
                                </span>
                                );
                            })}
                        </h1>
                    </span>
                </div>
            </div>
            <div className="col-sm-1 col-md-1 col-lg-1"></div>
        </div>
    )
}
export default TrabajosCurso
