import '../styleesheets/Login.css';
import logo from '../imagenes/logo-container.png'; 

function Login() {

    const handleLogin = (e) => {
        e.preventDefault();

        const data = {
            persona_id: username, // Supongo que estás usando "persona_id" como "username"
            contrasena: password
        };

        fetch('http://localhost:8010/login', { // Reemplaza con la URL correcta de tu endpoint de login
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(data)
        })
        .then(response => response.json())
        .then(data => {
            if (data.usuario) {
                // Hacer algo cuando el login sea exitoso, como redirigir al dashboard o guardar el usuario en el estado/context
            } else {
                // Hacer algo si hay un error en el login, como mostrar un mensaje de error
            }
        })
        .catch(error => {
            console.error('Error:', error);
        });
    }




    return (
        <div className='login-container'>

            <div className='login-form'>
                <img src={logo} alt='Header Graphic' className='header-image'/> {/* Usando logo-container como imagen de encabezado */}
                <input onChange={(event) => {setUsername(event.target.value)}} className='usuario-input' type='text' placeholder='Usuario'/>
                <input onChange={(event) => {setPassword(event.target.value)}} className='password-input' type='password' placeholder='Password'/>


                <div className='checkbox-container'>
                    <input type='checkbox' id='rememberMe'/>
                    <label htmlFor='rememberMe'>Recordar usuario</label>
                </div>
                <button className='iniciar-btn' onClick={handdlelogin}>Iniciar sesión</button>
            </div>

            <footer>
                Derechos Reservados Leonhardt Machinics Services
            </footer>
        </div>
    );
}

export default Login;
